const assignmentSchema = new mongoose.Schema({
    question: {
        type: String,
        required: true
    },
    answers: [
        {
            type: String,
            required: true
        }
    ]
});

const Assignment = mongoose.model('Assignment', assignmentSchema);

// Get all assignments from the database
Assignment.find({}, (err, assignments) => {
    // Loop through each assignment and print the question and answers
    assignments.forEach(assignment => {
        console.log('Question:', assignment.question);
        console.log('Answers:', assignment.answers);
    });
});
